import React, { FC, useEffect, useState } from 'react'
import { useLocation, useNavigate, Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleDown, faAngleUp } from '@fortawesome/free-solid-svg-icons'
import '../../styles/Shop/Shop.css'

interface ShopProps {
    windowWidth: number,
    allProducts: any,
}

const Shop: FC<ShopProps> = ({ windowWidth, allProducts }) => {
    const [isItASearchQuery, setIsItASearchQuery] = useState(false)
    const [searchQuery, setSearchQuery] = useState('')
    const [category, setCategory] = useState('')
    const location = useLocation()
    const queryParams = new URLSearchParams(location.search);
    const q = queryParams.get('q');

    useEffect(() => {
        console.log(allProducts)
        if (location.pathname === '/shop/all-products') {
            setCategory('All Products')
        } else if (location.pathname === '/shop/best-sellers') {
            setCategory('Best Sellers')
        } else if (location.pathname === '/shop/men') {
            setCategory('Men')
        } else if (location.pathname === '/shop/women') {
            setCategory('Women')
        } else if (location.pathname === '/shop/jewelry') {
            setCategory('Jewelry')
        } else if (q) {
            setIsItASearchQuery(true)
            setSearchQuery(q)
        }
    }, [location.pathname])

    return (
        <div className='shopWrapper'>
            <div className='shopContent'>
                <ol className='shopBreadCrumbsWrapper'>
                    <Link to='/' className='shopBreadCrumbItem'>Home</Link>
                    <span>/</span>
                    {isItASearchQuery ? (
                        <li className='shopBreadCrumbItem'>Search results for {searchQuery}</li>
                    ) : (
                        <li className='shopBreadCrumbItem'>{category}</li>
                    )}
                </ol>
                {isItASearchQuery ? (
                    <h2 className='shopCategoryTxt'>Search</h2>
                ) : (
                    <h2 className='shopCategoryTxt'>{category}</h2>
                )}
                <div className='resultsHeaderWrapper'>
                    <span className='resultsTxt'>Results: {allProducts.length} items</span>
                    <div className='filterBtnWrapper'>
                        <button className='filterBtn sort-by'>Sort by <FontAwesomeIcon icon={faAngleDown} /></button>
                    </div>
                </div>
                <div className='filterOptionsWrapper'>
                    <span className='filterTxt'>Filter:</span>
                    <div className='filterBtnWrapper'>
                        <button className='filterBtn'>Category <FontAwesomeIcon icon={faAngleDown} /></button>
                    </div>
                    <div className='filterBtnWrapper'>
                        <button className='filterBtn'>Price <FontAwesomeIcon icon={faAngleDown} /></button>
                    </div>
                    <div className='filterBtnWrapper'>
                        <button className='filterBtn'>Review Count <FontAwesomeIcon icon={faAngleDown} /></button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Shop
