import React, { useState, useEffect, FC } from 'react'
import '../styles/FeaturedProducts.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCartPlus } from '@fortawesome/free-solid-svg-icons'

interface FeaturedProductsProps {
    allProducts: any,
}

interface Product {
    name: string;
    price: number;
    image: string,
}

const FeaturedProducts: React.FC<FeaturedProductsProps> = ({ allProducts }) => {
    const [featuredProducts, setFeaturedProducts] = useState<Product[] | null>(null);

    useEffect(() => {
        const newArr:any = allProducts
        if (allProducts) {
            setFeaturedProducts(newArr.filter((product:any) => product.category !== "electronics").sort((a:any, b:any) => b.rating.rate - a.rating.rate))
        }
    },[allProducts])

    useEffect(() => {

    },[featuredProducts])

    if (featuredProducts === null) {
        return null;
    }

  return (
    <section className='featuredProductsWrapper'>
        <div className='featuredProductsContent'>
            {featuredProducts.slice(0,6).map((product, index) => (
                <div className='featuredProductContainer'>
                <img className='featuredProductImg' alt='featured product' src={product.image}/>
                <div className='featuredProductInfoWrapper'>
                    <div className='featuredProductLabelWrapper'>
                        <h6 className='featuredProductTitle'></h6>
                        <div className='featuredProductPriceWrapper'>
                            <span className='featuredProductPrice'></span>
                            <span className='featuredProductOriginalPrice'></span>
                        </div>
                    </div>
                    <div className='featuredProductShoppingCartWrapper'>
                        <FontAwesomeIcon icon={faCartPlus} />
                    </div>
                </div>
            </div>
            ))}
        </div>
    </section>
  )
}

export default FeaturedProducts
