import React, { useEffect, useState } from 'react'
import axios from 'axios'

export default function Products() {
    const categories = ['mens-shoes', 'mens-shirts', 'mens-watches', 'womens-dresses', 'womens-shoes', 'womens-watches', 
    'fragrances', 'skincare', 'sunglasses'];
    const allProducts: any = [];

    useEffect(() => {
        const fetchProductsByCategory = async (category: any) => {
            try {
                const response = await axios.get(`https://dummyjson.com/products/category/${category}`);
                if (response) {
                    return response.data.products;
                } else {
                    console.log('Error: Products Not Found!')
                }
            } catch (err) {
                console.log(err)
            }
        };
    
        const fetchAllProducts = async () => {
            for (const category of categories) {
                console.log(category)
                const products = await fetchProductsByCategory(category);
                console.log(products)
                allProducts.push(...products);
            }
            console.log(allProducts);
        };
        fetchAllProducts()
    },[])

    return { allProducts }
}
