import React, { useState, useEffect, FC, useRef } from 'react'
import '../../styles/Home/FeaturedProducts.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCartPlus, faStar, faAngleLeft, faAngleRight } from '@fortawesome/free-solid-svg-icons'
import { useLocation } from 'react-router-dom'

interface FeaturedProductsProps {
    allProducts: any,
    windowWidth: number
}

const FeaturedProducts: React.FC<FeaturedProductsProps> = ({ allProducts, windowWidth }) => {
    const imgRef = useRef<HTMLImageElement>(null);
    const [featuredProducts, setFeaturedProducts]: any = useState([]);
    const [imgBounds, setImgBounds] = useState<DOMRect | null>(null);
    const [currIndex, setCurrIndex] = useState(0)
    const [featuredProductsSliderLength, setFeaturedProductsSliderLength] = useState(2)
    const [paginationBarLength, setPaginationBarLength] = useState([1, 2])
    const [toggleUseEffectHook, setToggleUseEffectHook] = useState(false)

    useEffect(() => {
        const newArr: any = allProducts
        if (allProducts) {
            setFeaturedProducts(newArr.filter((product: any) => product.category !== "electronics").sort((a: any, b: any) => b.rating.rate - a.rating.rate).slice(0, 6))
        }
    }, [allProducts])

    useEffect(() => {
        if (imgRef.current) {
            const bounds = imgRef.current.getBoundingClientRect();
            setImgBounds(bounds);
            if (bounds.height !== bounds.width) {
                if (toggleUseEffectHook) {
                    setToggleUseEffectHook(false)
                } else {
                    setToggleUseEffectHook(true)
                }
            }
        }
        if (windowWidth <= 611) {
            setCurrIndex(0)
            setFeaturedProductsSliderLength(6)
            setPaginationBarLength([1, 2, 3, 4, 5, 6])
        } else if (windowWidth <= 1032) {
            setCurrIndex(0)
            setFeaturedProductsSliderLength(3)
            setPaginationBarLength([1, 2, 3])
        } else {
            setCurrIndex(0)
            setFeaturedProductsSliderLength(2)
            setPaginationBarLength([1, 2])
        }
    }, [imgRef, featuredProducts, windowWidth, toggleUseEffectHook]);

    const handlePrevSlide = () => {
        setCurrIndex((currIndex) => (currIndex === 0 ? featuredProductsSliderLength - 1 : currIndex - 1))
    }

    const handleNextSlide = () => {
        setCurrIndex((currIndex) => (currIndex === featuredProductsSliderLength - 1 ? 0 : currIndex + 1))
    }

    useEffect(() => {
        console.log(featuredProducts.length)
    },[featuredProducts])

    return (
        <section className='featuredProductsWrapper'>
            <div className='featuredProductsContent'>
                <div className='featuredProductsHeadingWrapper'>
                    <h2 className='featuredProductsHeadingTitle'>Featured Products</h2>
                    {featuredProducts && (
                        <div className='featuredProductsHeadingBtnsWrapper'>
                            <div className='featuredProductsHeadingBtn leftArrow' onClick={handlePrevSlide}>
                                <FontAwesomeIcon className='featuredProductsHeadingIcon' icon={faAngleLeft} />
                            </div>
                            <div className='featuredProductsHeadingBtn rightArrow' onClick={handleNextSlide}>
                                <FontAwesomeIcon className='featuredProductsHeadingIcon' icon={faAngleRight} />
                            </div>
                        </div>
                    )}
                </div>
                {(featuredProducts || featuredProducts.length === 0) ? (
                    <div className='renderedFeaturedProductsWrapper'>
                        <div className='renderedFeaturedProducts' style={{ transform: `translateX(-${currIndex * 100}%)` }}>
                            {featuredProducts?.map((product: any, index: any) => (
                                <div className='featuredProductContainer' key={index}>
                                    <img ref={imgRef} className='featuredProductImg' alt='featured product' src={product.image}
                                        style={{ height: imgBounds?.width }} onMouseDown={e => e.preventDefault()} />
                                    <span className='featuredProductRating'><FontAwesomeIcon icon={faStar} className='featuredProductRatingIcon' />
                                        {product.rating.rate}</span>
                                    <div className='featuredProductInfoWrapper'>
                                        <div className='featuredProductLabelWrapper'>
                                            <h6 className='featuredProductTitle'>{product.title}</h6>
                                            <span className='featuredProductPrice'>${product.price}</span>
                                        </div>
                                        <div className='featuredProductShoppingCartWrapper'>
                                            <FontAwesomeIcon icon={faCartPlus} className='featuredProductShoppingCart' />
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                ) : (
                    <div className='noProductsErrorWrapper'>
                        <h2 className='noProductsErrorTxt'>FAILED TO FETCH PRODUCTS</h2>
                    </div>
                )}
                {featuredProducts && (
                    <div className='featuredProductsPaginationBarWrapper'>
                        <div className='featuredProductsPaginationBarContent'>
                            <div className='paginationBarWrapper'>
                                {paginationBarLength.map((_, i) => (
                                    <div key={i} className='paginationBar' style={{
                                        backgroundColor: `${currIndex === i ? '#FFD700' : '#1b1b1b'}`,
                                        borderTopLeftRadius: `${i === 0 ? '5px' : '0px'}`,
                                        borderLeft: `${i === 0 ? 'none' : '1px solid #dadada'}`,
                                        borderBottomLeftRadius: `${i === 0 ? '5px' : '0px'}`,
                                        borderTopRightRadius: `${paginationBarLength.length - 1 === i ? '5px' : '0px'}`,
                                        borderBottomRightRadius: `${paginationBarLength.length - 1 === i ? '5px' : '0px'}`,
                                    }}
                                        onClick={() => setCurrIndex(i)} />
                                ))}
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </section>
    )
}

export default FeaturedProducts
