import React, { useState, useEffect, FC, useRef } from 'react'
import '../styles/FeaturedProducts.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCartPlus, faStar, faAngleLeft, faAngleRight } from '@fortawesome/free-solid-svg-icons'

interface FeaturedProductsProps {
    allProducts: any,
    windowWidth: number
}

interface Product {
    title: string;
    price: number;
    image: string,
    rating: Rating,
}

interface Rating {
    rate: number;
}

const FeaturedProducts: React.FC<FeaturedProductsProps> = ({ allProducts, windowWidth }) => {
    const imgRefs = useRef<Array<HTMLImageElement | null>>([]);
    const [featuredProducts, setFeaturedProducts] = useState<Product[] | null>(null);
    const [imgBounds, setImgBounds] = useState<(DOMRect | null)[]>([]);
    const [sliceStartingIndex, setSliceStartingIndex] = useState(0)
    const [sliceEndingIndex, setSliceEndingIndex] = useState(3)

    useEffect(() => {
        const newArr: any = allProducts
        if (allProducts) {
            setFeaturedProducts(newArr.filter((product: any) => product.category !== "electronics").sort((a: any, b: any) => b.rating.rate - a.rating.rate))
        }
    }, [allProducts])

    useEffect(() => {
        // Initialize refs array with null values equal to the number of products
        if (featuredProducts) {
            imgRefs.current = Array(featuredProducts.length).fill(null);
        }
      }, [featuredProducts]);

    useEffect(() => {
        // Create an array to store the bounds of each img element
        const boundsArray = imgRefs.current.map((imgRef) => {
          if (imgRef) {
            return imgRef.getBoundingClientRect();
          }
          return null;
        });
    
        // Set the imgBounds state with the boundsArray
        setImgBounds(boundsArray);
    
        // Log the widths of each img element
        boundsArray.forEach((bounds, index) => {
          console.log(`imgBounds[${index}] width: ${bounds?.width}`);
        });
    }, [imgRefs, featuredProducts, windowWidth, sliceEndingIndex]);
    

    useEffect(() => {
        console.log(featuredProducts)
    }, [featuredProducts])

    useEffect(() => {
        if (windowWidth >= 1032) {
            setSliceEndingIndex(3)
        } else if (windowWidth >= 800) {
            setSliceEndingIndex(2)
        }
    }, [windowWidth])

    if (!featuredProducts) {
        return <div>loading</div>;
    }

    return (
        <section className='featuredProductsWrapper'>
            <div className='featuredProductsContent'>
                <div className='featuredProductsHeadingWrapper'>
                    <h2 className='featuredProductsHeadingTitle'>Featured Products</h2>
                    <div className='featuredProductsHeadingBtnsWrapper'>
                        <div className='featuredProductsHeadingBtn leftArrow'>
                            <FontAwesomeIcon className='featuredProductsHeadingIcon' icon={faAngleLeft} />
                        </div>
                        <div className='featuredProductsHeadingBtn rightArrow'>
                            <FontAwesomeIcon className='featuredProductsHeadingIcon' icon={faAngleRight} />
                        </div>
                    </div>
                </div>
                <div className='renderedFeaturedProductsWrapper'>
                    {featuredProducts.slice(sliceStartingIndex, sliceEndingIndex).map((product, index) => (
                        <div className='featuredProductContainer' key={index}>
                            <img ref={(element) => {
                                imgRefs.current[index] = element;
                            }} className='featuredProductImg' alt='featured product' src={product.image}
                                style={{ height: imgBounds?.width }} onMouseDown={e => e.preventDefault()} />
                            <span className='featuredProductRating'><FontAwesomeIcon icon={faStar} className='featuredProductRatingIcon' />
                                {product.rating.rate}</span>
                            <div className='featuredProductInfoWrapper'>
                                <div className='featuredProductLabelWrapper'>
                                    <h6 className='featuredProductTitle'>{product.title}</h6>
                                    <span className='featuredProductPrice'>${product.price}</span>
                                </div>
                                <div className='featuredProductShoppingCartWrapper'>
                                    <FontAwesomeIcon icon={faCartPlus} className='featuredProductShoppingCart' />
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    )
}

export default FeaturedProducts
