import React, { useEffect, useState } from 'react'
import axios from 'axios'

export default function Products() {
    const categories = ['mens-shoes', 'mens-shirts', 'womens-dresses'];
    const allProducts: any = [];

    useEffect(() => {

        const fetchProductsByCategory = async (category: string): Promise<any[]> => {
            const response = await fetch(`http://localhost:5000/fetch-product-category?category=${category}`);
            const data = await response.json();
            return data.products;
          };

        const fetchAllProducts = async () => {
            for (const category of categories) {
              const products = await fetchProductsByCategory(category);
              allProducts.push(...products);
            }
            console.log(allProducts);
          };
          fetchAllProducts()
    },[])

    return { allProducts }
}
