import React, { FC, useState, useEffect } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleDown, faAngleUp, faStar } from '@fortawesome/free-solid-svg-icons'

interface ProductProps {
  windowWidth: number,
  allProducts: any,
  product: any
}

const Product: FC<ProductProps> = ({ windowWidth, allProducts, product }) => {
  const [category, setCategory] = useState('')
  const [relatedProducts, setRelatedProducts] = useState([])
  const roundedNum = Math.round(product.rating.rate)

  useEffect(() => {
    if (!allProducts || !product) return
    if (product.category === "men's clothing") {
      setCategory("Men")
      setRelatedProducts(allProducts.filter((product: any) => product.category === "men's clothing"))
    } else if (product.category === "women's clothing") {
      setCategory("Women")
      setRelatedProducts(allProducts.filter((product: any) => product.category === "women's clothing"))
    } else if (product.category === "jewelery") {
      setCategory("Jewelery")
      setRelatedProducts(allProducts.filter((product: any) => product.category === "jewelery"))
    }
  }, [allProducts, product])

  return (
    <div className='productWrapper'>
      <div className='productContent'>
        <ol className='productBreadCrumbsWrapper'>
          <Link to='/' className='productBreadCrumbItem'>Home</Link>
          <span className='productBreadCrumbItemSeperator'>/</span>
          <Link to={`/shop/${category.toLowerCase()}`}>{category}</Link>
          <span className='productBreadCrumbItemSeperator'>/</span>
          <li className='productBreadCrumbItem'>{product.title}</li>
        </ol>
        <h2 className='productTitle'>{product.title}</h2>
        <div className='productRatingsWrapper'>
          <FontAwesomeIcon className={`productStarIcon ${roundedNum >= 1 ? 'fullStar' : 'emptyStar'}`}
            icon={faStar} />
          <FontAwesomeIcon className={`productStarIcon ${roundedNum >= 2 ? 'fullStar' : 'emptyStar'}`}
            icon={faStar} />
          <FontAwesomeIcon className={`productStarIcon ${roundedNum >= 3 ? 'fullStar' : 'emptyStar'}`}
            icon={faStar} />
          <FontAwesomeIcon className={`productStarIcon ${roundedNum >= 4 ? 'fullStar' : 'emptyStar'}`}
            icon={faStar} />
          <FontAwesomeIcon className={`productStarIcon ${roundedNum >= 5 ? 'fullStar' : 'emptyStar'}`}
            icon={faStar} />
          <span className='productRatingCount'>({product.rating.count})</span>
        </div>
      </div>
    </div>
  )
}

export default Product
