import React, { useState, useEffect, useRef, lazy, Suspense } from 'react';
import { BrowserRouter, Route, Routes, useLocation, useNavigate } from 'react-router-dom';
import Products from './components/Products';

const NavBar = lazy(() => import('./components/NavBar'))
const Home = lazy(() => import('./components/Home/Home'))
const Shop = lazy(() => import('./components/Shop/Shop'))
const Footer = lazy(() => import('./components/Footer'))

const App = () => {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const { allProducts } = Products();
  const categories = ['women', 'men', 'jewelry', 'best-sellers', 'all-products']

  const RedirectToHome = (): any => {
    const navigate = useNavigate();
    React.useEffect(() => {
      navigate('/');
    }, [navigate]);
  }

  useEffect(() => {
    const handleWindowResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleWindowResize);

    return () => {
      window.removeEventListener('resize', handleWindowResize);
    };
  });

  if (!allProducts) {
    return <div>loading</div>;
  }

  return (
    <BrowserRouter>
      <Suspense fallback={<div className='suspenseLoaderWrapper'></div>}>
        <NavBar windowWidth={windowWidth} />
        <Routes>
          <Route path='/' element={<Home windowWidth={windowWidth} allProducts={allProducts} />} />
          {categories.map((category, index) => (
            <Route key={index} path={`/shop/${category}`}
              element={<Shop windowWidth={windowWidth} allProducts={allProducts} />}
            />
          ))}
          <Route path='/shop/search' element={<Shop windowWidth={windowWidth} allProducts={allProducts} />} />
          <Route path='*' element={<RedirectToHome />} />
        </Routes>
        <Footer />
      </Suspense>
    </BrowserRouter>
  );
}

export default App;
