import React, { FC, useState, useEffect, useRef } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleDown, faAngleUp, faStar, faMinus, faPlus, faHeart, faCartPlus, faAngleRight, faAngleLeft } from '@fortawesome/free-solid-svg-icons'
import '../../styles/Product/Product.css'

interface ProductProps {
  windowWidth: number,
  allProducts: any,
  product: any
}

const Product: FC<ProductProps> = ({ windowWidth, allProducts, product }) => {
  const [category, setCategory] = useState('')
  const [relatedProducts, setRelatedProducts] = useState([])
  const roundedNum = Math.round(product.rating.rate)
  const productColorOptions = ["white", "black", "gray", "blue", "yellow", "navy", "red", "purple"]
  const [chosenColor, setChosenColor] = useState('white')
  const [colorHoverIndex, setColorHoverIndex] = useState(9999)
  const productSizeOptions = ["XXS", "XS", "S", "M", "L", "XL", "XXL"]
  const [chosenSize, setChosenSize] = useState('S')
  const [sizeHoverIndex, setSizeHoverIndex] = useState(9999)
  const [quantity, setQuantity] = useState(1)
  const [overViewOpen, setOverViewOpen] = useState(false)
  const [currIndex, setCurrIndex] = useState(0)
  const [relatedProductsSliderLength, setRelatedProductsSliderLength] = useState(1)
  const imgRef = useRef<HTMLImageElement>(null);
  const [imgBounds, setImgBounds] = useState<DOMRect | null>(null);
  const [toggleUseEffectHook, setToggleUseEffectHook] = useState(false)
  const navigate = useNavigate()

  useEffect(() => {
    if (product.category === "men's clothing") {
      setCategory("Men")
      setRelatedProducts(allProducts.filter((allProduct: any) => allProduct.title !== product.title
        && allProduct.category.toLowerCase().includes("men") && allProduct.category !== "electronics"))
    } else if (product.category === "women's clothing") {
      setCategory("Women")
      setRelatedProducts(allProducts.filter((allProduct: any) => allProduct.title !== product.title
        && allProduct.category === "women's clothing"))
    } else if (product.category === "jewelery") {
      setCategory("Jewelery")
      setRelatedProducts(allProducts.filter((allProduct: any) => allProduct.title !== product.title).sort((a: any, b: any) => {
        if (a.category === "jewelery" && b.category !== "jewelery") {
          return -1;
        } else if (a.category !== "jewelery" && b.category === "jewelery") {
          return 1;
        } else {
          return 0;
        }
      }))
    }
  }, [allProducts, product])

  useEffect(() => {
    console.log(relatedProducts)
  }, [relatedProducts])

  const addToProductQuantity = () => {
    if (quantity >= 10) return
    setQuantity(quantity + 1)
  }

  const subtractFromProductQuantity = () => {
    if (quantity <= 1) return
    setQuantity(quantity - 1)
  }

  useEffect(() => {
    if (imgRef.current) {
      const bounds = imgRef.current.getBoundingClientRect();
      setImgBounds(bounds);
      if (bounds.height !== bounds.width) {
        if (toggleUseEffectHook) {
          setToggleUseEffectHook(false)
        } else {
          setToggleUseEffectHook(true)
        }
      }
    }
    if (windowWidth <= 611) {
      setCurrIndex(0)
    } else if (windowWidth <= 1032) {
      setCurrIndex(0)
    } else {
      setCurrIndex(0)
    }
  }, [imgRef, relatedProducts, windowWidth, toggleUseEffectHook]);

  const handlePrevSlide = () => {
    setCurrIndex((currIndex) => (currIndex === 0 ? relatedProductsSliderLength - 1 : currIndex - 1))
  }

  const handleNextSlide = () => {
    setCurrIndex((currIndex) => (currIndex === relatedProductsSliderLength - 1 ? 0 : currIndex + 1))
  }

  const handleProductNavigation = (productId: number) => {
    navigate(`/product/${productId}`)
  }

  return (
    <section className='productWrapper'>
      <div className='productContent'>
        <ol className='productBreadCrumbsWrapper'>
          <Link to='/' className='productBreadCrumbItem' onMouseDown={(e) => e.preventDefault()}>Home</Link>
          <span className='productBreadCrumbItemSeperator'>/</span>
          <Link to={`/shop/${category.toLowerCase()}`} className='productBreadCrumbItem'
            onMouseDown={(e) => e.preventDefault()}>{category}</Link>
          <span className='productBreadCrumbItemSeperator'>/</span>
          <li className='productBreadCrumbItem title'>{product.title}</li>
        </ol>
        <h2 className='productTitle'>{product.title}</h2>
        <div className='productRatingsWrapper'>
          <FontAwesomeIcon className={`productStarIcon ${roundedNum >= 1 ? 'fullStar' : 'emptyStar'}`}
            icon={faStar} />
          <FontAwesomeIcon className={`productStarIcon ${roundedNum >= 2 ? 'fullStar' : 'emptyStar'}`}
            icon={faStar} />
          <FontAwesomeIcon className={`productStarIcon ${roundedNum >= 3 ? 'fullStar' : 'emptyStar'}`}
            icon={faStar} />
          <FontAwesomeIcon className={`productStarIcon ${roundedNum >= 4 ? 'fullStar' : 'emptyStar'}`}
            icon={faStar} />
          <FontAwesomeIcon className={`productStarIcon ${roundedNum >= 5 ? 'fullStar' : 'emptyStar'}`}
            icon={faStar} />
          <span className='productRatingCount'>({product.rating.count} reviews)</span>
        </div>
        <div className='productMainContentWrapper'>
          <img src={product.image} className='productImg' alt='product' onMouseDown={(e) => e.preventDefault()} />
          <div className='productOptionsWrapper'>
            <div className='productColorOptionsWrapper'>
              <label className='productColorTxt'>Color: {chosenColor.toUpperCase()}</label>
              <div className='productColorsWrapper'>
                {productColorOptions.map((color, index) => (
                  <div className={`productColorBoxWrapper ${color === chosenColor ? 'chosen' : ''} 
                  ${colorHoverIndex === index ? 'mouseOver' : ''}`} key={index}>
                    <span className={`productColorBox ${color}`} onClick={() => setChosenColor(color)}
                      onMouseEnter={() => setColorHoverIndex(index)} onMouseLeave={() => setColorHoverIndex(100)}></span>
                  </div>
                ))}
              </div>
            </div>
            <div className='productSizesOptionsWrapper'>
              <label className='productSizeTxt'>Size: {chosenSize !== '' ? category.toUpperCase() : ''} {chosenSize}</label>
              <div className='productSizesWrapper'>
                {productSizeOptions.map((size, index) => (
                  <div className={`productSizeBoxWrapper ${chosenSize === size ? 'chosen' : ''} 
                    ${sizeHoverIndex === index ? 'mouseOver' : ''}`} key={index}>
                    <span className={`productSizeBox ${chosenSize === size ? 'chosen' : ''}`} onClick={() => setChosenSize(size)}
                      onMouseEnter={() => setSizeHoverIndex(index)} onMouseLeave={() => setSizeHoverIndex(100)}>{size}</span>
                  </div>
                ))}
              </div>
            </div>
            <div className='productControlOptionsWrapper'>
              <span className='productPrice'>${product.price}</span>
              <label className='productQuantityLabel'>Quantity</label>
              <div className='productAddQuantityWrapper'>
                <div className='productMinusIconWrapper' onClick={() => subtractFromProductQuantity()}>
                  <FontAwesomeIcon icon={faMinus} className='productMinusIcon' />
                </div>
                <input className='productQuantityInput' readOnly value={quantity} />
                <div className='productPlusIconWrapper' onClick={() => addToProductQuantity()}>
                  <FontAwesomeIcon icon={faPlus} className='productPlusIcon' />
                </div>
              </div>
              <div className='productAddBtnsWrapper'>
                <button className='productAddCartBtn'>ADD TO CART</button>
                <button className='productAddToWishListBtn'><FontAwesomeIcon icon={faHeart} className='productHeartIcon' /></button>
              </div>
            </div>
          </div>
        </div>
        <div className='productDescriptionWrapper'>
          <h2 className='productDescriptionTitle'>Description</h2>
          <div className='productOverViewWrapper'>
            <button className='productOverViewAccordionLabel' onClick={overViewOpen ? () => setOverViewOpen(false) :
              () => setOverViewOpen(true)}>
              <span className='overViewTxt'>Overview</span>
              {overViewOpen ? (
                <FontAwesomeIcon className='productOverViewIcon' icon={faMinus} />
              ) : (
                <FontAwesomeIcon className='productOverViewIcon' icon={faPlus} />
              )}
            </button>
            {overViewOpen && (
              <p className='productDescriptionTxt'>{product.description}</p>
            )}
          </div>
        </div>
        <div className='featuredProductsContent'>
          <div className='featuredProductsHeadingWrapper'>
            <h2 className='featuredProductsHeadingTitle'>Featured Products</h2>
            {relatedProducts.length !== 0 && (
              <div className='featuredProductsHeadingBtnsWrapper'>
                <div className='featuredProductsHeadingBtn leftArrow' onClick={handlePrevSlide}>
                  <FontAwesomeIcon className='featuredProductsHeadingIcon' icon={faAngleLeft} />
                </div>
                <div className='featuredProductsHeadingBtn rightArrow' onClick={handleNextSlide}>
                  <FontAwesomeIcon className='featuredProductsHeadingIcon' icon={faAngleRight} />
                </div>
              </div>
            )}
          </div>
          {relatedProducts.length !== 0 ? (
            <div className='renderedFeaturedProductsWrapper'>
              <div className='renderedFeaturedProducts' style={{ transform: `translateX(-${currIndex * 100}%)` }}>
                {relatedProducts?.map((product: any, index: any) => (
                  <div className='featuredProductContainer' key={index} onClick={() => handleProductNavigation(product.id)}>
                    <img ref={imgRef} className='featuredProductImg' alt='featured product' src={product.image}
                      style={{ height: imgBounds?.width }} onMouseDown={e => e.preventDefault()} />
                    <span className='featuredProductRating'><FontAwesomeIcon icon={faStar} className='featuredProductRatingIcon' />
                      {product.rating.rate}</span>
                    <div className='featuredProductInfoWrapper'>
                      <div className='featuredProductLabelWrapper'>
                        <h6 className='featuredProductTitle'>{product.title}</h6>
                        <span className='featuredProductPrice'>${product.price}</span>
                      </div>
                      <div className='featuredProductShoppingCartWrapper'>
                        <FontAwesomeIcon icon={faCartPlus} className='featuredProductShoppingCart' />
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div className='noProductsErrorWrapper'>
              <h2 className='noProductsErrorTxt'>FAILED TO FETCH PRODUCTS</h2>
            </div>
          )}
        </div>
      </div>
    </section>
  )
}

export default Product
