import { useState } from 'react'
import '../styles/Carousel.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleRight, faAngleLeft } from '@fortawesome/free-solid-svg-icons'


export default function Carousel({ children: slides }:any) {
    const [currIndex, setCurrIndex] = useState(0)

    const handlePrevSlide = () => {
        setCurrIndex((currIndex) => (currIndex === 0 ? slides.length - 1 : currIndex - 1))
    }
    
    const handleNextSlide = () => {
        setCurrIndex((currIndex) => (currIndex === slides.length - 1 ? 0 : currIndex + 1))
    }
    return (
        <div className='carouselWrapper'>
            <div className='carouselSlides'>
                {slides}
            </div>
            <div className='carouselBtnsWrapper'>
                <button>
                <FontAwesomeIcon icon={faAngleLeft} className='navBarLogoIcon' onClick={() => handlePrevSlide()}/>
                </button>
                <button>
                <FontAwesomeIcon icon={faAngleRight} className='navBarLogoIcon' onClick={() => handleNextSlide}/>
                </button>
            </div>
        </div>
    )
}
