import React, { FC, useEffect, useState } from 'react'
import { useLocation, useNavigate, Link } from 'react-router-dom';
import '../../styles/Shop/Shop.css'

interface ShopProps {
    windowWidth: number,
    allProducts: any,
}

const Shop: FC<ShopProps> = ({ windowWidth, allProducts }) => {
    const [isItASearchQuery, setIsItASearchQuery] = useState(false)
    const [category, setCategory] = useState('')
    const [breadCrumbTxt, setBreadCrumTxt] = useState('')
    const location = useLocation()

    useEffect(() => {
        console.log(location.pathname.split(' / '))
        if (location.pathname === '/shop/all-products') {
            setCategory('all-products')
            setBreadCrumTxt('All Products')
        } else if (location.pathname === '/shop/best-sellers') {
            setCategory('best-sellers')
            setBreadCrumTxt('Best Sellers')
        } else if (location.pathname === '/shop/men') {
            setCategory('men')
            setBreadCrumTxt('Men')
        } else if (location.pathname === '/shop/women') {
            setCategory('women')
            setBreadCrumTxt('Women')
        } else if (location.pathname === '/shop/jewelry') {
            setCategory('jewelry')
            setBreadCrumTxt('Jewelry')
        }
    },[location.pathname])

    return (
        <div className='shopWrapper'>
            <div className='shopContent'>
                <ol className='shopBreadCrumbsWrapper'>
                    <Link to='/' className='shopBreadCrumbItem'>Home</Link>
                    <li className='shopBreadCrumbItem'>{breadCrumbTxt}</li>
                </ol>
            </div>
        </div>
    )
}

export default Shop
