import React, { FC, useEffect, useState } from 'react'
import '../../styles/Shop/DropDownMenu.css'

interface DropDownMenuProps {
    typeOfFilter: string
    handleSortByFilter: Function,
    sortByFilterOption: string
}

const DropDownMenu: FC<DropDownMenuProps> = ({ typeOfFilter, handleSortByFilter, sortByFilterOption }) => {
    const [sortByFilter, SetSortByFilter] = useState(false)

    useEffect(() => {
        if (typeOfFilter === 'Sort By') {
            SetSortByFilter(true)
        } 
    },[])
  return (
    <div className='dropDownMenuWrapper'>
        <div className='dropDownMenuContent'>
            {sortByFilter && (
                <ul className='sortByOptionsWrapper'>
                    <li className='sortByOption topRated' onClick={() => handleSortByFilter('Top Rated')}>Top Rated</li>
                    <li className='sortByOption' onClick={() => handleSortByFilter('Low to high')}>Price: Low to high</li>
                    <li className='sortByOption' onClick={() => handleSortByFilter('High to low')}>Price: High to low</li>
                </ul>
            )}
        </div>
    </div>
  )
}

export default DropDownMenu
