import React, { FC, useEffect, useState } from 'react'
import '../../styles/Shop/DropDownMenu.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheck } from '@fortawesome/free-solid-svg-icons'

interface DropDownMenuProps {
    typeOfFilter: string
    handleSortByFilter: Function,
    sortByFilterOption: string,
    handleCategoryFilter: Function,
    categoryFilterOption: string,
}

const DropDownMenu: FC<DropDownMenuProps> = ({ typeOfFilter, handleSortByFilter, sortByFilterOption, categoryFilterOption,
    handleCategoryFilter }) => {
    const [sortByFilter, SetSortByFilter] = useState(false)
    const [categoryFilter, setCategoryFilter] = useState(false)

    useEffect(() => {
        if (typeOfFilter === 'Sort By') {
            SetSortByFilter(true)
        }
    }, [])

    return (
        <div className='dropDownMenuWrapper'>
            <div className='dropDownMenuContent'>
                {sortByFilter && (
                    <ul className='sortByOptionsWrapper'>
                        <li className={`sortByOption topRated ${sortByFilterOption === 'Top Rated' ? 'filterOn' : ''}`}
                            onClick={() => handleSortByFilter('Top Rated')}>Top Rated
                            {sortByFilterOption === 'Top Rated' ? <FontAwesomeIcon icon={faCheck} /> : null}</li>
                        <li className={`sortByOption ${sortByFilterOption === 'Low to high' ? 'filterOn' : ''}`}
                            onClick={() => handleSortByFilter('Low to high')}>Price: Low to high
                            {sortByFilterOption === 'Low to high' ? <FontAwesomeIcon icon={faCheck} /> : null}</li>
                        <li className={`sortByOption ${sortByFilterOption === 'High to low' ? 'filterOn' : ''}`}
                            onClick={() => handleSortByFilter('High to low')}>Price: High to low
                            {sortByFilterOption === 'High to low' ? <FontAwesomeIcon icon={faCheck} /> : null}</li>
                    </ul>
                )}
                {categoryFilter && (
                    <ul className='categoryOptionsWrapper'>
                        <li className={`categoryOption topRated ${sortByFilterOption === 'Top Rated' ? 'filterOn' : ''}`}
                            onClick={() => handleCategoryFilter('Top Rated')}>Top Rated
                            {sortByFilterOption === 'Top Rated' ? <FontAwesomeIcon icon={faCheck} /> : null}</li>
                        <li className={`categoryOption ${sortByFilterOption === 'Low to high' ? 'filterOn' : ''}`}
                            onClick={() => handleCategoryFilter('Low to high')}>Price: Low to high
                            {sortByFilterOption === 'Low to high' ? <FontAwesomeIcon icon={faCheck} /> : null}</li>
                        <li className={`categoryOption ${sortByFilterOption === 'High to low' ? 'filterOn' : ''}`}
                            onClick={() => handleCategoryFilter('High to low')}>Price: High to low
                            {sortByFilterOption === 'High to low' ? <FontAwesomeIcon icon={faCheck} /> : null}</li>
                    </ul>
                )}
            </div>
        </div>
    )
}

export default DropDownMenu
